package controller;

import model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;


@WebServlet(name = "CustomerServlet", urlPatterns = {"", "/"})
public class CustomerServlet extends HttpServlet {

    static List<Customer> customers = new ArrayList<>();
    static {
        customers.add( new Customer("Trần Thị Hoàn","1996/13/01","Đà Nẵng","data:image/jpeg;base64,"));
        customers.add( new Customer("Bành Thị Hải","1989/18/09","Đà Nẵng","data:image/jpeg;base64,"));
        customers.add( new Customer("Lê Thị Trí","2000/18/09","Đà Nẵng","data:image/jpeg;base64,"));
        customers.add( new Customer("Ngyễn Thị Tâm","1997/18/09","Đà Nẵng","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcToK4nxR6Kgyny2_jrcGXW8tTpkXywBQrB2mQ&usqp=CAU"));
        customers.add( new Customer("Trần Thị Phúc","2003/18/09","Đà Nẵng","data:image/jpeg;base64,"));
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setAttribute("customers", customers);
//      Gửi dữ liệu qua trang JSP.
        request.getRequestDispatcher("index.jsp").forward(request,response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }
}
